import React from 'react';

export const TextlabelTypes = {
    PARAGRAPH: 'p',
    HEADING1: 'h1',
    HEADING2: 'h2',
    HEADING3: 'h3',
    HEADING4: 'h4',
};

export const TextLabelAlignment = {
    LEFT: 'left',
    RIGHT: 'right',
    CENTER: 'center'
}

const TextLabel = ({ type, fontSize, text, alignment }) => {
    const elementType = TextlabelTypes[type] || TextlabelTypes.PARAGRAPH;
    const alignmentType = TextLabelAlignment[type] || TextLabelAlignment.LEFT;

    const elementStyle = {
        fontSize: type === TextlabelTypes.PARAGRAPH ? fontSize : undefined,
    };

    return (
        <div>
            {React.createElement(elementType, { style: elementStyle }, text)}
        </div>
    );
}

export default TextLabel;

