import React from 'react';

const labelTypes = {
    PARAGRAPH: 'p',
    HEADING1: 'h1',
    HEADING2: 'h2',
    HEADING3: 'h3',
    HEADING4: 'h4',
};

const TextLabel = ({ type, fontSize, text }) => {
    const elementType = labelTypes[type] || labelTypes.PARAGRAPH;

    const elementStyle = {
        fontSize: type === labelTypes.PARAGRAPH ? fontSize : undefined,
    };

    return (
        <div>
            {React.createElement(elementType, { style: elementStyle }, text)}
        </div>
    );
}

export default TextLabel;

/*
* Example use:
